INSERT INTO `lol_summoners` (`id`, `summoner_name`, `summoner_icerik`, `summoner_image`) VALUES
(1, 'Berrak Zihin (Clarity)', 'Şampiyonunun ve yakındaki dostlarının azami mana değerini %40 oranında doldurur.', 'clarity'),
(15, 'Bariyer (Barrier)', 'Şampiyonuna 2 saniyeliğine, seviyesine bağlı olarak 115-455 oranında kalkan verir.', 'barrier'),
(2, 'Bitkinlik (Exhaust)', 'Hedef şampiyonu 2,5 saniyeliğine bitkin düşürerek bu süre boyunca hareket hızını ve verilen hasarı %30, saldırı hızını da %50 azaltır.', 'exhaust'),
(3, 'Garnizon (Garrison)', 'Dost kule: Kullanıldığında 8 saniye boyunca kulelerin atak hızını ve rejenerasyonu büyük ölçüde artırır.<br>Düşman kule: Kullanıldığında 8 saniye boyunca düşman kulesinin atak hızını %80 oranında düşürür.', 'garrison'),
(4, 'Hayalet (Ghost)', 'Karakteriniz 10 saniye boyunca %27 oranında daha hızlı koşar ve bu süreçte minionlara takılmaz.', 'ghost'),
(5, 'Şifa (Heal)', 'Şampiyonunun ve yakınındaki arkadaşlarının Can değerini şampiyon seviyesine bağlı olarak 90 - 345 arasında yeniler.', 'heal'),
(6, 'Dirilt (Revive)', 'Şampiyonunu hemen takımına ait Başlangıç Platformunda diriltir ve şampiyonunun hareket hızını kısa süreliğine artırır.', 'revive'),
(7, 'Çarp (Smite)', 'Levelinize göre minionlara 490-1000 gerçek hasar verir.', 'smite'),
(8, 'Büyüt (Surge)', '12 saniye boyunca %35 oranında attack speed ve levelinize göre 10-78 Ability power verir. (Bu özellik sezon 2''de gelen bir yamayla beraber oyundan kaldırılmıştır.)', 'surge'),
(9, 'Işınlan (Teleport)', 'Kullanıldıktan 4 saniye sonra, dost kule, minion veya toteme ışınlanırsınız.', 'teleport'),
(10, 'Arındır (Cleanse)', 'Üzerinizde bulunan kötü büyüleri temizler ve 3 saniye boyunca kötü büyülere karşı %65 oranında bağışıklık kazandırır.', 'cleanse'),
(11, 'Tutuştur (Ignite)', 'Kullanılan düşmana 5 saniye boyunca toplamda 70-410 sabit hasar verir ve hedefin alacağı tüm can ve mana bonuslarını %50 oranında düşürür.', 'ignite'),
(12, 'Güçlendir (Promote)', 'Yanınızdaki bir siege minionu güçlendirip ona bonus can ve stat verir. Bu minionun öldürdüğü her minionun parası size gelir. (Bu özellik sezon 2''de gelen bir yamayla beraber oyundan kaldırılmıştır.)', 'promote'),
(13, 'Durugörü (Clairvoyance)', '4 saniye boyunca kullanılan bölgeyi aydınlatır. Haritanın herhangi bir yerine kullanılabilir.', 'clairvoyance'),
(14, 'Sıçra (Flash)', 'Şampiyonunun, imlecinin doğrultusunda, kısa bir mesafe ışınlanmasını sağlar. ', 'flash');
